package operators;

public class ravno {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 0;
		int b = 1;
		System.out.println(a == b);
		System.out.println(a != b);
		System.out.println(a > b);
		System.out.println(a < b);
		System.out.println(a <= b);
		boolean c = a < b && b < 2;
		System.out.println("сравнение " + c);
 
	}

}
